css:
@page {
    padding: 0;
    margin: 0;
}
*{
    font-size:9.5px;
}
body{
    padding:0;
    margin:0;
    font-family: Arial, Helvetica, sans-serif;
}

.print-format{
    padding:10px;
    font-size: inherit;
}

.print-format label {
    font-size: inherit;
}

header {
  display: flex;
  justify-content: space-between;
}
header .containerr {
  align-items: center;
  display: flex;
  gap:10px;
}
header .text {
  display: flex;
  flex-direction: column;
  align-items: end;
  justify-content: center;
}
header .text h3 {
  margin: 0;
}

.container-inputs {
  width: 100%;
  display: flex;
  padding: 4px;
  gap:10px;
}
.container-inputs .input-1 {
  width: 33.3%;
  line-height: 5px;
}
.container-inputs .input-1 .first {
  display: flex;
  gap: 5px;
}
.container-inputs .input-1 .first label {
font-weight: 800;
line-height:1;
    margin-top: 20px;
    color: black;
}
.container-inputs .input-1 .first span {
  width: 150px;
  line-height:1;
  display:flex;
  align-items:end;
  justify-content:center ;
  border-bottom: 2px solid black;
  text-align: center;
}
.container-inputs .input-2 {
  width: 33.3%;
  line-height: 5px;
}
.container-inputs .input-2 .first {
  display: flex;
  gap: 5px;
}
.container-inputs .input-2 .first label {
    line-height:1;
font-weight: 800;
    margin-top: 20px;
    color: black;
}
.container-inputs .input-2 .first span {
  width: 150px;
  display:flex;
  line-height:1;
  align-items:end;
  justify-content:center ;
  border-bottom: 2px solid black;
  text-align: center;
}
.container-inputs .input-3 {
  width: 33.3%;
  line-height: 5px;
}
.container-inputs .input-3 .first {
  display: flex;
  gap: 5px;
}
.container-inputs .input-3 .first label {
font-weight: 800;
    margin-top: 20px;
    line-height:1;
    color: black;
}
.container-inputs .input-3 .first span {
   width: 150px;
  display:flex;
  line-height:1;
  align-items:end;
  justify-content:center ;
  border-bottom: 2px solid black;
  text-align: center;
}

.container-table {
  display: flex;
  width:100%;
  font-size:8px;
}
.container-table .first-table {
  width: 100% !important;
}
.container-table .first-table table {
  width: 100% !important;
}
.container-table .first-table table{
    border:none !important;
}
.container-table .first-table table,
.container-table .first-table th,
.container-table .first-table td {
border: 1px solid black;
    /*border-collapse: collapse;*/
     border-spacing: 0 2px; /* Adjust this value for the vertical space */
        border-collapse: separate;
    padding: 1px !important;
    font-size: inherit;
    font-weight: 700;
    color: black;
}
.container-table .first-table table tr th,
.container-table .first-table th tr th,
.container-table .first-table td tr th {
  text-align: center;
  padding: 7px;
}
.container-table .first-table table tr td,
.container-table .first-table th tr td,
.container-table .first-table td tr td {
  text-align: center;
 padding: 7px !important;
}

.container-1 {
  display: flex;
  width: 100%;
  margin-left: 0%;
}
.container-1 .box {
  border: 1px solid black;
  flex: 1;
}
.container-1 .box .box-title {
  padding: 5px 10px;
  margin: 0;
  justify-content: center;
  display: flex;
}
.container-1 .box ul {
  margin: 0;
  padding: 0px 0px 0px 15px;
}
.container-1 .box h4{
    font-size: inherit;
}
.container-2 {
  margin-top: 10px;
  position:fixed;
  bottom:20px;
  width:98%;
}
.container-2 .container-child {
  width: 100%;
  display: flex;
  height:15px;
  border: 1px solid black;
}
.container-2 .container-child .first {
  width: 50%;
}
.container-2 .container-child .first p {
  margin: 0px;
    color: red;
    font-weight:bold;
    width: 200%;
}
.container-2 .container-child .second span {
  width: 170px;
  /*border: 1px solid black;*/
}
.container-2 .container-child .second span p {
  margin: 0px;
}
.container-2 .container-child-1 {
  width: 100%;
  display: flex;
  height:20px;
  border: 1px solid black;
}
.container-2 .container-child-1 .first {
  width: 58.5%;
}
.container-2 .container-child-1 .first p {
  margin: 0px;
}
.container-2 .container-child-1 .second {
  display: flex;
  width: 50%;
}
.container-2 .container-child-1 .second span {
  width: 200px;
  border: 1px solid black;
}
.container-2 .container-child-1 .second span p {
  margin: 0px;
}
.container-2 .container-child-2 {
  width: 100%;
  display: flex;
  height: 20px;
  border: 1px solid black;
}
.container-2 .container-child-2 .first {
  width: 50%;
}
.container-2 .container-child-2 .first p {
  margin: 0px;
}
.container-2 .container-child-2 .second {
  display: flex;
  justify-content: space-around;
  width: 50%;
}
.container-2 .container-child-2 .second span p {
  margin: 0px;
}
.container-2 .container-child-3 {
  width: 100%;
  display: flex;
  border: 1px solid black;
}
.container-2 .container-child-3 .first {
  display: flex;
  gap: 150px;
  border: 1px solid black;
  width: 50%;
}
.container-2 .container-child-3 .first p {
  margin: 0px;
}
.container-2 .container-child-3 .second {
  border: 1px solid black;
  display: flex;
  width: 50%;
}
.container-2 .container-child-3 .second span p {
  margin: 0px;
}
.container-2 .container-child-4 {
  width: 100%;
  display: flex;
  border: 1px solid black;
}
.container-2 .container-child-4 .first {
  border: 1px solid black;
  width: 50%;
}
.container-2 .container-child-4 .first p {
  margin: 0px;
}
.container-2 .container-child-4 .second {
  border: 1px solid black;
  display: flex;
  width: 50%;
}
.container-2 .container-child-4 .second span p {
  margin: 0px;
}
