css:
.print-format {}

.print-format th, .print-format td {
  padding: 5px !important;
}

.dd th {
  background-color: #f2f2f2;
}


th {
  font-weight: bold !important;
  color: black !important;
}

#header {
  display: flex;
  border-bottom: 2px solid rgb(248, 222, 90);
  width: 100%;
}

.logo {
  width: 50%;
}

.con {
  text-align: center;
  width: 50%;
}

.con .lead {
  font-size: 22px;
}

h2 {
  text-align: center;
}

#body {
  border-bottom: 2px solid grey;
}

#body h3 {
  font-size: 25px;
}

table {
  width: 100%;
  border-collapse: collapse;
}

th,
td {
  text-align: left;
  padding: 10px 0px;
}

/* Top Info */
.employee-info table {
  width: 100%;
  margin-top: 20px;
  margin-bottom: 40px;
}

/* Side-by-side section */
.earnings-deductions {
  display: flex;
  justify-content: space-between;
  gap: 20px;
  margin-bottom:50px;
}

.ingresos,
.descuentos {
  width: 50%;
}

.dd th,
.dd td,
.bb th,
.bb td {
  border: 1px solid black;
  padding: 8px;
}

.bb th {
  background-color: #f2f2f2;
}

.box {
  padding-top: 40px;
  border: 2px solid #000;
  width: 60%;
  text-align: center;
  margin: 50px auto 0 auto; /* Horizontally centered */
}
.bottom{
    width:100%;
    height:100px;
}
.bottom th{
    width: 20%;
    text-align: right;
}
.bottom td{
    width: 20%;
}

